import { Link } from "react-router-dom";

function CharacterDetails ({findCharacter}) {
    if(!findCharacter) {
        return (
            <section>

            </section>
        )
    }
    return (
    <section className="screen">
        <Link to={`/`}><p className="return">Volver</p></Link>
        <section className="imageBox">

            <img className="imageWindow" src= {findCharacter.image} alt={`Foto de ${findCharacter.name} `} />
            <div className="info">
            <h3 className="window">{findCharacter.name}</h3>
            <p className="window">{findCharacter.status}</p>
            <p className="window">{findCharacter.species}</p>
            <p className="window">{findCharacter.origin}</p>
            <p className="window">Episodios : {findCharacter.episode.length}</p>
            </div>
        </section>
    </section>
    ) 
}
export default CharacterDetails;
